<?php
require_once 'class.user.php';
$user = new USER();

if(empty($_GET['id']) && empty($_GET['code']))
{
 $user->redirect('index.php');
}

if(isset($_GET['id']) && isset($_GET['code']))
{
 $id = base64_decode($_GET['id']);
 $code = $_GET['code'];
 
 $statusY = "Y";
 $statusN = "N";
 
 $stmt = $user->runQuery("SELECT id,userStatus FROM users WHERE id=:uID AND tokenCode=:code LIMIT 1");
 $stmt->execute(array(":uID"=>$id,":code"=>$code));
 $row=$stmt->fetch(PDO::FETCH_ASSOC);
 if($stmt->rowCount() > 0)
 {
  if($row['userStatus']==$statusN)
  {
   $stmt = $user->runQuery("UPDATE users SET userStatus=:status WHERE id=:uID");
   $stmt->bindparam(":status",$statusY);
   $stmt->bindparam(":uID",$id);
   $stmt->execute(); 
   
   $msg = "<h2>Your Account is Now Activated.<br><img src='data:image/svg+xml;base64,' /></h2><a href='index.php'><button class='btn-back'>Login Now</button></a>"; 
  }
  else
  {
   $msg = "<h2><strong>Hey !</strong>  Your Account is already Activated.<br><img src='data:image/svg+xml;base64,' /></h2><a href='index.php'><button class='btn-back'>Login Now</button></a>";
  }
 }
 else
 {
  $msg = "<h2><strong>Sorry !</strong>  No Account Found.<br><img src='data:image/svg+xml;base64,' /></h2><a href='signup.php'><button class='btn-back'>Signup Here</button></a>";
 } 
}

